package lista_03import java.util.Scannerfun main(args: Array<String>) {    val input = Scanner(System.`in`)    val horaInicial = input.nextInt()    val minInicial = input.nextInt()    val horaFinal = input.nextInt()    val minFinal = input.nextInt()    var duracaoHora = when {        (horaFinal > horaInicial) -> horaFinal - horaInicial        (horaFinal == horaInicial) -> {            if(minInicial < minFinal){                0            }else{                24            }        }        else -> (24 - horaInicial) + horaFinal    }    var duracaoMin = when {        minFinal > minInicial -> minFinal - minInicial        minFinal == minInicial -> 0        else -> {            duracaoHora -= 1            var minT = minFinal - minInicial            minT + 60        }    }    if(!(duracaoHora <= 0 && duracaoMin <= 0)){        println("O JOGO DUROU $duracaoHora HORA(S) E $duracaoMin MINUTO(S)")    }}